import React, { useState, useRef } from "react";

import Controls from "./Controls";
import Track from "./Track";
import ProgressBar from "./ProgressBar";
import TimeTrack from "./TimeTrack";
import { Backward, Forward } from "./Icons";

const AudioPlayer = ({
  trackTitle,
  progressBar,
  tracks,
  navigator,
  timeTrack,
}) => {
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [currentTrack, setCurrentTrack] = useState(tracks[currentTrackIndex]);

  const [timeProgress, setTimeProgress] = useState(0);
  const [duration, setDuration] = useState(0);

  const audioRef = useRef();
  const progressBarRef = useRef();

  const handleNextTrack = () => {
    const nextTrackIndex = currentTrackIndex + 1;
    if (nextTrackIndex < tracks.length) {
      setCurrentTrackIndex(nextTrackIndex);
      setCurrentTrack(tracks[nextTrackIndex]);
    }
  };

  const handlePreviousTrack = () => {
    const prevTrackIndex = currentTrackIndex - 1;
    if (prevTrackIndex < tracks.length) {
      setCurrentTrackIndex(prevTrackIndex);
      setCurrentTrack(tracks[prevTrackIndex]);
    }
  };

  return (
    <div className="audio-player">
      <div className="inner">
        <Track
          {...{
            currentTrack,
            audioRef,
            setDuration,
            progressBarRef,
            trackTitle,
          }}
        />
        {progressBar && <ProgressBar {...{ progressBarRef, audioRef }} />}

        <Controls
          {...{ audioRef, progressBarRef, duration, setTimeProgress }}
        />
        {navigator && (
          <>
            <button onClick={handlePreviousTrack} className="prev">
              <Backward />
            </button>
            <button onClick={handleNextTrack} className="next">
              <Forward />
            </button>
          </>
        )}
        {timeTrack && (
          <TimeTrack {...{ timeProgress, duration, setTimeProgress }} />
        )}
      </div>
    </div>
  );
};

export default AudioPlayer;
